$(document).ready((
    function(){
        AOS.init(),$("[data-bs-hover-animate]").mouseenter((
            function(){
                var e=$(this);
                e.addClass("animated "+e.attr("data-bs-hover-animate"))})).mouseleave((function(){
                    var e=$(this);e.removeClass("animated "+e.attr("data-bs-hover-animate"))
                }))
            }));

      